  function callFlickr(data, callback){
    $.ajax({
      type: "GET",
      url: "https://api.flickr.com/services/rest/",
      data: data,
      dataType: 'jsonp',
      jsonp: 'jsoncallback',
      success: function(data){
        callback(data);
      },
      error: function(){
      }
    });
  }


  function formatDate(date){
    var months = new Array("January", "February", "March", 
      "April", "May", "June", "July", "August", "September", 
      "October", "November", "December"
    );

    var dayOfMonth = date.getUTCDate();
    var month = date.getUTCMonth();
    var hour = date.getUTCHours() + 9;
    var minutes = date.getUTCMinutes();
    var suffix = "";
    var am_pm;

    //add the proper suffix (st, nd, rd) to the numerical day of the month
    switch(dayOfMonth) {
      case 1:
      case 21: 
      case 31:
       suffix = "st";
       break;
      case 2:
      case 22:
       suffix = "nd";
       break;
      case 3: 
      case 23:
       suffix = "rd";
       break;
      default:
      suffix = "th";
    }

    if (minutes == 0){
      minutes = '00';
    }
    //format the time into 12 hour format with proper AM PM
    if (hour < 12)
       {
       am_pm = "AM";
       }
    else
       {
       am_pm = "PM";
       }

    if (hour == 0){
        hour = 12;
       }
    if (hour > 12){
        hour = hour - 12;
       }

    return months[month] +" "+ dayOfMonth + suffix + ", " + hour + ":" + minutes + " " + am_pm;
  }



