  (function() {
    function createPlayer(jqe, video, options) {
        var ifr = $('iframe', jqe);
        if (ifr.length === 0) {
            ifr = $('<iframe scrolling="no">');
            ifr.addClass('player');
            var videoContainer = $('<div/>');
            videoContainer.addClass('video-container');
            videoContainer.append(ifr);
        }
        var src = 'https://www.youtube.com/embed/' + video.id;
        if (options.playopts) {
            src += '?';
            for (var k in options.playopts) {
                src += k + '=' + options.playopts[k] + '&';
            }
            src += '_a=b';
        }
        $('.video-title').html(video.title);
        $('.video-description').html(video.desc);
        ifr.attr('src', src);
        
        jqe.prepend(videoContainer);

    }

    function createCarousel(jqe, videos, options) {
      var car = $('div.video-carousel', jqe);
      if (car.length === 0) {
        car = $('<div>');
        car.addClass('video-carousel');
        var carouselWrap = $('<div/>');
        carouselWrap.addClass('carousel-wrap');
        carouselWrap.append(car);
        jqe.append(carouselWrap);
    }

      $.each(videos, function(i, video) {
          options.thumbnail(car, video, options);
      });
    }

    function createDetails(jqe, video, options){
      var details = $('div.video-details', jqe);
       if (details.length === 0) {
        details = $('<div/>');
        details.addClass('video-details');
        var title = $('<h3/>');
        title.addClass('video-title');
        var desc = $('<p/>');
        desc.addClass('video-description');
        details.append(title);
        details.append(desc);
        jqe.prepend(details);
      }
    }

    function createThumbnail(jqe, video, options) {
      
        var imgurl = video.thumbnails.high.url;
        var img = $('img[src="' + imgurl + '"]');
        var desc;
        var mycontainer = $('<div/>');
        mycontainer.addClass('thumb-container');  
        if (img.length !== 0) return;
        img = $('<img/>');
        img.addClass('thumbnail');
        mycontainer.append(img);
        img.attr('src', imgurl);
        img.attr('title', video.title);
        desc = $('<p class="yt-title">' + video.title + '</p>');
        mycontainer.append(desc);
        jqe.append(mycontainer);
        
        mycontainer.click(function() {
            options.player(options.maindiv, video, $.extend(true, {}, options, {
                playopts: {
                    autoplay: 1
                }
            }));
           
        });
    }

    var defoptions = {
      autoplay: false,
      user: null,
      carousel: createCarousel,
      details: createDetails,
      player: createPlayer,
      thumbnail: createThumbnail,
      loaded: function() {
        var thumbs = $('.thumb-container').length;
        var thumbWidth = $('.thumb-container:first').width() + 40;
        var containerWidth = thumbs * thumbWidth;
   
        $('.video-carousel').width(containerWidth);
      },
      playopts: {
          autoplay: 0,
          egm: 1,
          autohide: 1,
          fs: 1,
          showinfo: 1
      }
    };

    $.fn.extend({
      youTubeChannel: function(options) {
        var md = $(this);
        md.addClass('youtube');
        md.addClass('youtube-channel');
        var allopts = $.extend(true, {}, defoptions, options);
        allopts.maindiv = md;
        $.getJSON('https://www.googleapis.com/youtube/v3/channels?part=contentDetails&id='+allopts.user+'&key=AIzaSyAbI6T7s1vZtb_DY4pRWnuq-kZIqJNUa6E', null, function(data) {

          var playlistId = data.items[0].contentDetails.relatedPlaylists.uploads;

          $.getJSON('https://www.googleapis.com/youtube/v3/playlistItems?part=snippet&maxResults=50&playlistId='+playlistId+'&key=AIzaSyAbI6T7s1vZtb_DY4pRWnuq-kZIqJNUa6E', null, function(data){

            var videos = [];
         
            $.each(data.items, function(i, item) {
              var snippet = item.snippet;

              var video = {
                  title: snippet.title,
                  id: snippet.resourceId.videoId,
                  desc: snippet.description,
                  thumbnails: snippet.thumbnails
              };
              videos.push(video);
            });
            allopts.allvideos = videos;
            allopts.carousel(md, videos, allopts);
            allopts.details(md, videos[0], allopts);
            allopts.player(md, videos[0], allopts);
            allopts.loaded(videos, allopts);
          });
        });
      }
    });
  })();

